function M.c_from_ast(ast)
  assert(ast.tag == 'Block')
  local cobj = to_c(ast)

  return [[
#include <stdio.h>
]] .. (cobj.toplevel and cobj.toplevel or '') .. [[
int main() {
]] .. cobj[1] ..
[[
return 0;
}
]]
end

function M.c_from_lua(input)
  local Parser = require 'luafish.parser'
  local p = Parser()
  local ast = p:parse(input)
  return M.c_from_ast(ast)
end

function M.object_to_ast(o)
  if type(o) == 'string' then
    return ASTNode {'String', o}
  elseif type(o) == 'number' then
    return ASTNode {'Number', o}
  elseif o == nil then
    return ASTNode {'Nil'}
  elseif o == false then
    return ASTNode {'False'}
  elseif o == true then
    return ASTNode {'True'}
  elseif type(o) == 'table' then
    --LIMITATION: only does numeric keys + tag since non-numeric keys can
    -- be recursive
    local elements = {}
    elements[#elements+1] = 'Table'
    if o.tag ~= nil then
      elements[#elements+1] = ASTNode {'Field', M.object_to_ast 'tag', M.object_to_ast(o.tag)}
    end
    for k,v in ipairs(o) do
      elements[#elements+1] = ASTNode {'Field',
         M.object_to_ast(k), M.object_to_ast(v)}
    end
    return ASTNode(elements)
  else
    assert(false, "NOT IMPLEMENTED for " .. tostring(o))
  end
end

