function funcs.ExpList(ast)
  local ts = {}
  for i=1,#ast do
    ts[#ts+1] = convert(ast[i])
  end
  return table.concat(ts, ',')
end

function funcs.Op(ast)
  local id = ast[1]
  local code = tocode[id]
  if #ast == 3 then
    return convert(ast[2]) .. ' ' .. code .. ' ' .. convert(ast[3])
  elseif #ast == 2 then
    return code .. ' ' .. convert(ast[2])
  else assert(false) end
end

if #ast == 3 then
